function Bullet(startX, startY, xDir, yDir, identity) {
	this.x = startX;
	this.y = startY;
	this.unitVectX = xDir;
	this.unitVectY = yDir;
	this.identity = identity;
	this.shouldExist = true;
	this.timeUp = 0;
}

Bullet.LIFESPAN = 1000;
Bullet.HITBOX = 5;

Bullet.create = function(startX, startY, xDir, yDir, identity) {
	return new Bullet(startX, startY, xDir, yDir, identity);
}

Bullet.prototype.update = function() {
	this.timeUp++;
	if (this.x < 0 || this.x > 800 || this.y < 0 || this.y > 600 || this.timeUp > Bullet.LIFESPAN) {
		this.shouldExist = false;
	} else {
		if (this.x + 10 * this.unitVectX >= 800 || this.x + 10 * this.unitVectX <= 0) {
			this.unitVectX = -this.unitVectX;
		}
		if (this.y + 10 * this.unitVectY >= 600 || this.y + 10 * this.unitVectY <= 0) {
			this.unitVectY = -this.unitVectY;
		}
		this.x += 10 * this.unitVectX;
		this.y += 10 * this.unitVectY;
	}
}

module.exports = Bullet;