function Player(startX, startY, orientation, name, id) {
	this.x = startX;
	this.y = startY;
	this.unitVectX = 0;
	this.unitVectY = 0;
	this.velocity = [0,0];
	this.name = name;
	this.id = id;
}

Player.generateNewPlayer = function(name, id) {
	return new Player(300,300,0, name, id);
}

Player.prototype.moveToLocation = function(targetX, targetY) {
	console.log(this.x);
	console.log(this.y);
	console.log('got to moveToLocation');
	var vectorX = targetX - this.x;
	var vectorY = targetY - this.y;
	var length = Math.sqrt(vectorX * vectorX + vectorY * vectorY);
	this.unitVectX = vectorX / length;
	this.unitVectY = vectorY / length;
	this.x += 2 * this.unitVectX;
	this.y += 2 * this.unitVectY;
	console.log(this.x);
	console.log(this.y);
}

module.exports = Player;