function Player(startX, startY, orientation, name, id) {
	this.x = startX;
	this.y = startY;
	this.orientation = orientation;
	this.velocity = [0,0];
	this.name = name;
	this.id = id;
}

Player.generateNewPlayer = function(name, id) {
	return new Player(500,300,0, name, id);
}

Player.prototype.moveToLocation = function(targetX, targetY) {
	console.log(this.x);
	console.log(this.y);
	console.log('got to moveToLocation');
	var vectorX = targetX - this.x;
	var vectorY = targetY - this.y;
	var length = Math.sqrt(vectorX * vectorX + vectorY * vectorY);
	var unitVectX = vectorX / length;
	var unitVectY = vectorY / length;
	this.x += 2 * unitVectX;
	this.y += 2 * unitVectY;
	console.log(this.x);
	console.log(this.y);
}

module.exports = Player;